import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import { Button, TextField, Select, MenuItem, Card, CardContent, Typography, CircularProgress } from '@mui/material';
import './App.css';

const FOURSQUARE_API_KEY = "fsq3soZNlLbhRbl7blf3576+sKcYYa4YJt2/JO+WXGqcp/8=";
const OPENCAGE_API_KEY = "92aec43ed55d40efb1fd4e8282e3c9ca";
const DEFAULT_LOCATION = "Ramallah";

function App() {
  const [places, setPlaces] = useState([]);
  const [favorites, setFavorites] = useState([]);
  const [query, setQuery] = useState("");
  const [location, setLocation] = useState(DEFAULT_LOCATION);
  const [loading, setLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");
  const [ratingFilter, setRatingFilter] = useState(0);

  // ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÖŸÅÿ∂ŸÑÿ© ŸÖŸÜ localStorage ÿπŸÜÿØ ÿ®ÿØÿ° ÿßŸÑÿ™ÿ∑ÿ®ŸäŸÇ
  useEffect(() => {
    const savedFavorites = localStorage.getItem('favorites');
    if (savedFavorites) {
      setFavorites(JSON.parse(savedFavorites));
    }
  }, []);

  // ÿ≠ŸÅÿ∏ ÿßŸÑŸÖŸÅÿ∂ŸÑÿ© ŸÅŸä localStorage ÿπŸÜÿØ ÿßŸÑÿ™ÿ∫ŸäŸäÿ±
  useEffect(() => {
    localStorage.setItem('favorites', JSON.stringify(favorites));
  }, [favorites]);

  const fetchCoordinates = async (placeName) => {
    try {
      const response = await axios.get("https://api.opencagedata.com/geocode/v1/json", {
        params: {
          key: OPENCAGE_API_KEY,
          q: placeName,
          limit: 1,
        },
      });
      const result = response.data.results[0];
      return result ? { lat: result.geometry.lat, lon: result.geometry.lng } : null;
    } catch {
      return null;
    }
  };

  const fetchPlacePhoto = async (fsq_id) => {
    try {
      const response = await axios.get(
        `https://api.foursquare.com/v3/places/${fsq_id}/photos`,
        {
          headers: {
            Authorization: FOURSQUARE_API_KEY,
          },
        }
      );
      const photo = response.data[0];
      return photo ? `${photo.prefix}original${photo.suffix}` : null;
    } catch {
      return null;
    }
  };

  const fetchPlaces = async () => {
    setLoading(true);
    setErrorMessage("");
    const coords = await fetchCoordinates(location);
    if (!coords) {
      setErrorMessage("‚ùå Location not found. Please try another name.");
      setLoading(false);
      return;
    }

    try {
      const response = await axios.get(
        "https://api.foursquare.com/v3/places/search",
        {
          headers: {
            Authorization: FOURSQUARE_API_KEY,
          },
          params: {
            ll: `${coords.lat},${coords.lon}`,
            ...(query && { query }),
            limit: 10,
          },
        }
      );

      const resultsWithPhotos = await Promise.all(
        response.data.results.map(async (place) => {
          const photoUrl = await fetchPlacePhoto(place.fsq_id);
          return { ...place, photo: photoUrl };
        })
      );

      // ŸÅŸÑÿ™ÿ±ÿ© ÿßŸÑÿ£ŸÖÿßŸÉŸÜ ÿ®ŸÜÿßÿ°Ÿã ÿπŸÑŸâ ÿßŸÑÿ™ŸÇŸäŸäŸÖ
      const filteredPlaces = resultsWithPhotos.filter(place => {
        const rating = place.rating || 0; // ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿßŸÑÿ™ŸÇŸäŸäŸÖ ÿ•ÿ∞ÿß ŸÉÿßŸÜ ŸÖŸàÿ¨ŸàÿØÿßŸã
        return rating >= ratingFilter;
      });

      setPlaces(filteredPlaces);
    } catch (error) {
      console.error("Error fetching places:", error);
      setErrorMessage("‚ùå Failed to fetch places.");
    }
    setLoading(false);
  };

  const toggleFavorite = (place) => {
    if (favorites.some(fav => fav.fsq_id === place.fsq_id)) {
      setFavorites(favorites.filter(fav => fav.fsq_id !== place.fsq_id));
    } else {
      setFavorites([...favorites, place]);
    }
  };

  return (
    <Router>
      <div className="container">
        <nav>
          <Link to="/">Home</Link> | 
          <Link to="/favorites">Favorites ({favorites.length})</Link>
        </nav>

        <Routes>
          <Route path="/" element={
            <>
              <h1>Nearby Places</h1>
              <div className="controls">
                <TextField
                  label="Enter city or area"
                  variant="outlined"
                  value={location}
                  onChange={(e) => setLocation(e.target.value)}
                  style={{ marginBottom: '10px', width: '300px' }}
                />
                <Button 
                  variant="contained" 
                  color="primary" 
                  onClick={fetchPlaces} 
                  style={{ marginBottom: '10px' }}
                >
                  üîç Search This Location
                </Button>
                <Select
                  label="Category"
                  value={query}
                  onChange={(e) => setQuery(e.target.value)}
                  style={{ width: '300px', marginBottom: '10px' }}
                >
                  <MenuItem value="">All</MenuItem>
                  <MenuItem value="cafe">Caf√©s</MenuItem>
                  <MenuItem value="restaurant">Restaurants</MenuItem>
                  <MenuItem value="resort">Resorts</MenuItem>
                </Select>

                <Select
                  label="Rating Filter"
                  value={ratingFilter}
                  onChange={(e) => setRatingFilter(parseFloat(e.target.value))}
                  style={{ width: '300px', marginBottom: '10px' }}
                >
                  <MenuItem value={0}>All Ratings</MenuItem>
                  <MenuItem value={4}>4 stars or more</MenuItem>
                  <MenuItem value={4.5}>4.5 stars or more</MenuItem>
                  <MenuItem value={5}>5 stars</MenuItem>
                </Select>
              </div>

              {errorMessage && <div className="error-box">{errorMessage}</div>}

              {loading ? (
                <CircularProgress />
              ) : (
                <div className="cards">
                  {places.map((place) => (
                    <Card key={place.fsq_id} sx={{ maxWidth: 345, margin: '10px' }}>
                      <button 
                        onClick={() => toggleFavorite(place)}
                        className={`favorite-btn ${favorites.some(fav => fav.fsq_id === place.fsq_id) ? 'active' : ''}`}
                      >
                        ‚ù§Ô∏è
                      </button>
                      {place.photo ? (
                        <img src={place.photo} alt={place.name} style={{ width: '100%', height: '200px', objectFit: 'cover' }} />
                      ) : place.categories[0]?.icon ? (
                        <img
                          src={`${place.categories[0].icon.prefix}bg_64${place.categories[0].icon.suffix}`}
                          alt={place.categories[0].name}
                          style={{ width: '100%', height: '200px', objectFit: 'cover' }}
                        />
                      ) : null}
                      <CardContent>
                        <Typography variant="h5">{place.name}</Typography>
                        <Typography color="textSecondary" variant="body2">
                          Category: {place.categories[0]?.name || "N/A"}
                        </Typography>
                        <Typography color="primary">
                          Rating: {place.rating || "N/A"} ‚≠ê
                        </Typography>
                      </CardContent>
                    </Card>
                  ))}
                </div>
              )}
            </>
          } />

          <Route path="/favorites" element={
            <div>
              <h1>Favorite Places</h1>
              {favorites.length === 0 ? (
                <p>No favorites yet. Add some from the home page!</p>
              ) : (
                <div className="cards">
                  {favorites.map((place) => (
                    <Card key={place.fsq_id} sx={{ maxWidth: 345, margin: '10px' }}>
                      {place.photo ? (
                        <img src={place.photo} alt={place.name} style={{ width: '100%', height: '200px', objectFit: 'cover' }} />
                      ) : place.categories[0]?.icon ? (
                        <img
                          src={`${place.categories[0].icon.prefix}bg_64${place.categories[0].icon.suffix}`}
                          alt={place.categories[0].name}
                          style={{ width: '100%', height: '200px', objectFit: 'cover' }}
                        />
                      ) : null}
                      <CardContent>
                        <Typography variant="h5">{place.name}</Typography>
                        <Typography color="textSecondary" variant="body2">
                          Category: {place.categories[0]?.name || "N/A"}
                        </Typography>
                      </CardContent>
                    </Card>
                  ))}
                </div>
              )}
            </div>
          } />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
