import "./App.css";
import React, { useEffect, useState } from "react";
import axios from "axios";

const FOURSQUARE_API_KEY = "fsq3soZNlLbhRbl7blf3576+sKcYYa4YJt2/JO+WXGqcp/8=";
const DEFAULT_COORDS = { lat: 31.9632, lng: 35.9304 };

function App() {
  const [places, setPlaces] = useState([]);
  const [query, setQuery] = useState("cafe");
  const [coords, setCoords] = useState(DEFAULT_COORDS);
  const [loading, setLoading] = useState(false);

  const fetchPlacePhoto = async (fsq_id) => {
    try {
      const response = await axios.get(`https://api.foursquare.com/v3/places/${fsq_id}/photos`, {
        headers: { Authorization: FOURSQUARE_API_KEY }
      });
      const photo = response.data[0];
      return photo
        ? `${photo.prefix}original${photo.suffix}`
        : null;
    } catch {
      return null;
    }
  };

  useEffect(() => {
    fetchPlaces();
  }, [coords, query]);

  const getLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setCoords({
            lat: position.coords.latitude,
            lng: position.coords.longitude,
          });
        },
        () => {
          alert("Location access denied. Using default location.");
        }
      );
    }
  };

  return (
    <div className="container">
      <h1>Nearby Places</h1>
      <div className="controls">
        <button onClick={getLocation}>üìç Use My Location</button>
        <select onChange={(e) => setQuery(e.target.value)} value={query}>
          <option value="">All</option>
          <option value="cafe">Caf√©s</option>
          <option value="restaurant">Restaurants</option>
          <option value="resort">Resorts</option>
        </select>
      </div>

      {loading ? (
        <p>Loading...</p>
      ) : (
        <div className="cards">
          {places.map((place) => (
            <div key={place.fsq_id} className="card">
              {place.categories[0]?.icon && (
                <img
                  src={`${place.categories[0].icon.prefix}bg_64${place.categories[0].icon.suffix}`}
                  alt={place.categories[0].name}
                />
              )}
              <div>
                <h3>{place.name}</h3>
                <p className="category">
                  <strong>Category:</strong> {place.categories[0]?.name}
                </p>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default App;
