import React, { useEffect, useState } from 'react';
import axios from 'axios';

const FOURSQUARE_API_KEY = "fsq3soZNlLbhRbl7blf3576+sKcYYa4YJt2/JO+WXGqcp/8=";
const DEFAULT_COORDS = { lat: 31.9632, lng: 35.9304 };

function App() {
  const [places, setPlaces] = useState([]);
  const [query, setQuery] = useState("cafe");
  const [coords, setCoords] = useState(DEFAULT_COORDS);
  const [loading, setLoading] = useState(false);

  const fetchPlaces = async () => {
    setLoading(true);
    try {
      const response = await axios.get("https://api.foursquare.com/v3/places/search", {
        headers: {
          Authorization: FOURSQUARE_API_KEY,
        },
        params: {
          ll: `${coords.lat},${coords.lng}`,
          query: query,
          limit: 10,
        },
      });
      setPlaces(response.data.results);
    } catch (error) {
      console.error("Error fetching places:", error);
    }
    setLoading(false);
  };

  useEffect(() => {
    fetchPlaces();
  }, [coords, query]);

  const getLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setCoords({
            lat: position.coords.latitude,
            lng: position.coords.longitude,
          });
        },
        () => {
          alert("Location access denied. Using default location.");
        }
      );
    }
  };

  return (
    <div style={{ padding: "2rem", fontFamily: "Arial" }}>
      <h1>Nearby Places</h1>
      <button onClick={getLocation}>üìç Use My Location</button>
      <select onChange={(e) => setQuery(e.target.value)} value={query} style={{ marginLeft: "1rem" }}>
        <option value="cafe">Caf√©s</option>
        <option value="restaurant">Restaurants</option>
        <option value="resort">Resorts</option>
      </select>

      {loading ? <p>Loading...</p> : (
        <div style={{ marginTop: "2rem" }}>
          {places.map((place) => (
            <div key={place.fsq_id} style={{ border: "1px solid #ccc", padding: "1rem", marginBottom: "1rem" }}>
              <h3>{place.name}</h3>
              {place.categories[0]?.icon && (
                <img
                  src={`${place.categories[0].icon.prefix}bg_64${place.categories[0].icon.suffix}`}
                  alt={place.categories[0].name}
                />
              )}
              <p><strong>Category:</strong> {place.categories[0]?.name}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default App;
